def do_algebra_helper(operator, operand, index, current_expression):
    if len(operator) == 0 or len(operand) == 1:
        return current_expression
    if index == len(operand):
        raise TypeError("operand list is not long enough")

    current_expression.append(operand[index])
    current_expression.extend(operator)

    left_value = do_algebra_helper(operator, operand, index + 1, current_expression)

    current_expression = current_expression[:-1]
    current_expression.append(left_value)
    current_expression.extend(operator)

    right_value = do_algebra_helper(operator, operand, index + 1, current_expression)

