from typing import List
def factorize(n: int) -> List[int]:
    factors = []
    while n % 2 == 0:
        factors.append(2)
        n //= 2
    while n % 3 == 0:
        factors.append(3)
        n //= 3

    for i in range(5, int(n ** 0.5) + 1, 6):
        while n % i == 0:
            factors.append(i)
            n //= i
        while n % (i + 2) == 0:
            factors.append(i + 2)
            n //= i + 2
    if n!= 1:
        raise ValueError("Number not divisible by any of its prime factors")
    return factors

```

