from typing import List

def factorize(n: int) -> List[int]:
    prime_factors: List[int] = []
    while n > 1:
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                prime_factors.append(i)
                n = n // i
                break
        else:
            prime_factors.append(n)
            break
    return prime_factors
