def is_prime(n: int):
    """
    is_prime returns True if n is prime. False if n is not prime
    or n is not an integer.
    """

    # if n is not an integer
    if not isinstance(n, int):
        return False

    # if n is a small prime
    if n in [2, 3, 5, 7]:
        return True

    # if n is odd and n > 1
    if n % 2!= 0 and n > 1:
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True

    # if n is even and n > 2
    if n % 2 == 0 and n > 2:
        for i in range(3, int(n ** 0.5) + 1, 2):
            if n % i == 0:
                return False
        return True
