def largest_prime_factor(n: int):
    """Return the largest prime factor of n. Assume n > 1 and is not a prime."""

    # if the number is already known to be prime, there are no prime factors other than 1 and itself
    if n in [2, 3]:
        return 1

    # test numbers are less than or equal to the square root of n. If not, then the rest of the divisors are greater than n and do not have prime factors
    for i in range(2, int(n ** 0.5) + 1):
        # if number is divided by some factor, then it is not prime
        if n % i == 0:
            # if the divisor factor is itself prime, return it
            if i in [2, 3]:
                return i
            # if not prime, find the largest prime factor of the divisor factor
            else:
                return largest_prime_factor(i)

    return 1


