def encode(message):
    """
    Takes a message, and encodes in such a way that it swaps case of all letters, 
    replaces all vowels in the message with the letter that appears 2 places ahead of 
    that vowel in the english alphabet. 
    Assume only letters. 
    """
    new_list = []
    swap_case = False
    for letter in message:
        if letter == letter.lower():
            swap_case = True
        elif letter.isalpha():
            if letter.isupper():
                new_list.append(chr(ord(letter)+32))
            else:
                new_list.append(chr(ord(letter)-32))
        else:
            new_list.append(letter)

        if letter == 'e' or letter == 'E' or letter == 'i' or letter == 'I' or letter == 'o' or letter == 'O' or letter == 'u' or letter == 'U':
            vowel_swap = False
            if letter == 'e' or letter == 'E':
                new_list.append(chr(ord('t')))
                vowel_swap = True
            elif letter == 'i' or letter == 'I':
                new_list.append(chr(ord('w')))
                vowel_swap = True
            elif letter == 'o' or letter == 'O':
                new_list.append(chr(ord('p')))
                vowel_swap = True
            elif letter == 'u' or letter == 'U':
                new_list.append(chr(ord('v')))
                vowel_swap = True
            else:
                new_list.append(letter)
        else:
            new_list.append(letter)
    if swap_case:
        return ''.join(new_list).swapcase()
    else:
        return ''.join(new_list)
