def longestSubstring(string):

    seen = set(string[0])
    longest = string[0]

    for i in range(1,len(string)):
        if string[i] in seen:
            seen.remove(string[i-1])
        seen.add(string[i])
        longest = max(longest, string[i-len(seen)+1:i+1])
